mapper.select:
SELECT *
FROM notifications
WHERE user_id = #{userId}
ORDER BY send_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM notifications
WHERE user_id = #{userId}

SELECT *
FROM notifications_pickup
WHERE user_id = #{pickupUserId}
ORDER BY send_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM notifications_pickup
WHERE user_id = #{pickupUserId}
